import React from 'react';
import { useCart } from './CartContext';

function PanierSummary() {
  const { cartCount } = useCart();
  // Supposons que vous ayez un tableau de produits dans le panier avec leur prix
  const panier = [
    { nom: 'Produit 1', prix: 10.99 },
    { nom: 'Produit 2', prix: 19.99 },
    // ... ajoutez d'autres produits ici
  ];

  const total = panier.reduce((acc, item) => acc + item.prix, 0);

  return (
    <aside>
      <h2>Résumé du Panier</h2>
      <ul>
        {panier.map((item, index) => (
          <li key={index}>
            {item.nom}: ${item.prix.toFixed(2)}
          </li>
        ))}
      </ul>
      <p>Total: ${total.toFixed(2)}</p>
      <p>Nombre d'articles dans le panier: {cartCount}</p>
    </aside>
  );
}

export default PanierSummary;
