import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import Compteur from './Compteur';
import { CartProvider } from './CartContext';

const ProductList = () => {
  const [datas, setDatas] = useState([]);


  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((data) => setDatas(data));
  };

  return (
    <CartProvider>
      <section className="py-5">
        <Container>
          <Row className="justify-content-center">
            {datas.map((product, index) => (
              <Col key={index} md={3} className="mb-5">
                <Card className="h-100">
                  {/* ... autres éléments du produit ... */}
                  <Card.Footer className="p-4 pt-0 border-top-0 bg-transparent">
                    <div className="text-center">
                      <Button variant="outline-dark mt-auto">View options</Button>
                      <Compteur />
                    </div>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        </Container>
      </section>
      <PanierSummary /> {/* Affichez le résumé du panier ici */}
    </CartProvider>
  );
};

export default ProductList;
