import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Navbar from './NavBar';
import Header from './Header';
import ProductList from './ProductList';
import CartSummary from '../component/CartSummary';
import ProductDetails from './ProductDetails';
import Footer from './Footer';

const initialProducts = [
  // Liste de vos produits
];

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [showCartSummary, setShowCartSummary] = useState(false);

  const handleCartClick = () => {
    setShowCartSummary(!showCartSummary);
  };

  return (
    <Router>
      <div className="App">
        <Navbar cartItemsCount={cartItems.length} onCartClick={handleCartClick} />
        {showCartSummary && <CartSummary cartItems={cartItems} onClose={handleCartClick} />}
        <Switch>
          <Route path="/products">
            <ProductList products={initialProducts} />
          </Route>
          <Route path="/product/:id">
            <ProductDetails />
          </Route>
          <Route path="/">
            <Header />
            {/* ... Autre contenu de la page d'accueil */}
          </Route>
        </Switch>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
