import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';


const products = [
    {
      id: 1,
      name: 'Produit 1',
      description: 'Description du produit 1',
      price: '40.00 - 80.00',
      imageUrl: 'URL_de_l_image_du_produit_1',
    },
    {
      id: 2,
      name: 'Produit 2',
      description: 'Description du produit 2',
      price: '40.00 - 80.00',
      imageUrl: 'URL_de_l_image_du_produit_2',
    },
    // Ajoutez d'autres produits ici
  ];
  


const ProductList = () => {

const[datas, setDatas] = useState([]);


    useEffect(() =>{
        fetchData();
}, []);

const fetchData = () => {
    fetch("https://fakestoreapi.com/products")
    .then((response) => response.json())
    .then((data) => setDatas(data));
}


  return (
    <section className="py-5">
      <Container>
        <Row className="justify-content-center">
            {datas.map((product, index) => (
          <Col key={index} md={3} className="mb-5">
            <Card className="h-100">
              <Card.Img variant="top" src={product.image} alt="..." />
              <Card.Body className="p-4">
                <div className="text-center">
                  <h5 className="fw-bolder">{product.title}</h5>
                  <p>{product.description}</p>
                  <p>{product.price}</p>
                </div>
              </Card.Body>
              <Card.Footer className="p-4 pt-0 border-top-0 bg-transparent">
                <div className="text-center"><Button variant="outline-dark mt-auto">View options</Button></div>
              </Card.Footer>
            </Card>
          </Col>
 ) )}

        </Row>

        
      </Container>
    </section>

    
  );
};

export default ProductList;
