import React, { createContext, useContext, useState } from 'react';
import Button from 'react-bootstrap/Button'; // Importez le composant Button depuis React Bootstrap
import { CartProvider, CartContext } from './CartContext';
import { useCart } from './CartContext';

function Compteur() {
  const { cartCount, addToCart } = useContext(CartContext);


  return (
    <div>
      <Button variant="outline-dark">
        <i className="bi bi-cart-fill me-1"></i>
        Cart
        <span className="badge bg-dark text-white ms-1 rounded-pill">{cartCount}</span>
      </Button>

      <Button variant="outline-dark mt-auto" onClick={() =>addToCart}>
        Ajouter au panier
      </Button>
    </div>
  );
}

export default Compteur;
