import React, { useState } from 'react';

function MonComposant() {
  // Créez un état local pour suivre le nombre dans le badge
  const [nombreDansPanier, setNombreDansPanier] = useState(0);

  // Fonction pour incrémenter le nombre dans le panier
  const ajouterAuPanier = () => {
    setNombreDansPanier(nombreDansPanier + 1);
  };

  return (
    <div>
      <Button variant="outline-dark">
        <i className="bi bi-cart-fill me-1"></i>
        Cart
        <span className="badge bg-dark text-white ms-1 rounded-pill">{nombreDansPanier}</span>
      </Button>

      <Button variant="outline-dark mt-auto" onClick={ajouterAuPanier}>
        Ajouter au panier
      </Button>
    </div>
  );
}

export default MonComposant;
